list-view-config.query-select:
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#> PREFIX core: <http://vivoweb.org/ontology/core#> PREFIX afn: <http://jena.hpl.hp.com/ARQ/function#>
SELECT DISTINCT ?subclass # send the property to the template, since this view supports multiple role properties ?property ?role ?startDateOnly ?roleLabel ?activity ?activityName ?activityLabel ?infoResource ?infoResourceName ?infoResourceLabel ?dateTimeStart ?dateTimeEnd WHERE {
?subject ?property ?role
OPTIONAL { ?role core:roleIn ?activity LET (?activityName := afn:localname(?activity)) OPTIONAL { ?activity rdfs:label ?activityLabel } }
OPTIONAL { ?role core:forInformationResource ?infoResource LET (?infoResourceName := afn:localname(?infoResource)) OPTIONAL { ?infoResource rdfs:label ?infoResourceLabel } }
OPTIONAL { ?role a ?subclass . ?subclass rdfs:subClassOf core:Role }
OPTIONAL { ?role rdfs:label ?roleLabel } OPTIONAL { ?role core:dateTimeInterval ?dateTimeInterval OPTIONAL { ?dateTimeInterval core:start ?dateTimeStartValue . ?dateTimeStartValue core:dateTime ?dateTimeStart } OPTIONAL { ?dateTimeInterval core:end ?dateTimeEndValue . ?dateTimeEndValue core:dateTime ?dateTimeEnd } }
FILTER ( bound(?infoResource) )
} ORDER BY ?subclass DESC(?dateTimeEnd) DESC(?dateTimeStart) ?infoResourceName

list-view-config.query-construct:
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>  
PREFIX core: <http://vivoweb.org/ontology/core#>  
CONSTRUCT {
    ?subclass rdfs:subClassOf core:Role 
} WHERE {
    ?subclass rdfs:subClassOf core:Role 
}

PREFIX core: <http://vivoweb.org/ontology/core#>
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>    
CONSTRUCT { 
    ?subject ?property ?role .
    ?role ?roleProperty ?roleValue . 
    ?activity rdfs:label ?activityName 
} WHERE {
    {
       ?subject ?property ?role 
    } UNION {
       ?subject ?property ?role .
       ?role ?roleProperty ?roleValue 
    } UNION {
       ?subject ?property ?role .
       ?role core:roleIn ?activity . 
       ?activity rdfs:label ?activityName 
    }
}

PREFIX core: <http://vivoweb.org/ontology/core#>   
CONSTRUCT { 
    ?subject ?property ?role .  
    ?role core:dateTimeInterval ?dateTimeInterval .
    ?dateTimeInterval core:start ?dateTimeStartValue .
    ?dateTimeStartValue core:dateTime ?dateTimeStart 
} WHERE {
    ?subject ?property ?role .  
    ?role core:dateTimeInterval ?dateTimeInterval .
    ?dateTimeInterval core:start ?dateTimeStartValue .
    ?dateTimeStartValue core:dateTime ?dateTimeStart 
}

PREFIX core: <http://vivoweb.org/ontology/core#>   
CONSTRUCT { 
    ?subject ?property ?role .  
    ?role core:dateTimeInterval ?dateTimeInterval .
    ?dateTimeInterval core:end ?dateTimeEndValue .
    ?dateTimeEndValue core:dateTime ?dateTimeEnd 
} WHERE {
    ?subject ?property ?role .  
    ?role core:dateTimeInterval ?dateTimeInterval .
    ?dateTimeInterval core:end ?dateTimeEndValue .
    ?dateTimeEndValue core:dateTime ?dateTimeEnd 
}

PREFIX core: <http://vivoweb.org/ontology/core#>
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>    
CONSTRUCT { 
    ?subject ?property ?role .
    ?role ?roleProperty ?roleValue . 
    ?infoResource rdfs:label ?infoResourceLabel 
} WHERE {
    {
        ?subject ?property ?role 
    } UNION {
        ?subject ?property ?role .
        ?role ?roleProperty ?roleValue .
    } UNION {
        ?subject ?property ?role .
        ?role core:forInformationResource ?infoResource . 
        ?infoResource rdfs:label ?infoResourceLabel .  
    }
}
